// could generate random address to use at runtime
pub static BRAINDANCE_CONTROLLER: Lazy<rAddress> = Lazy::new(|| {
    "0xC0ff33C0ffeeC0ff33C0ffeeC0ff33C0ff33C0ff"
        .parse()
        .unwrap()
});

